class i{constructor(){this.heap=[]}swap(t,h){const s=this.heap[t];this.heap[t]=this.heap[h],this.heap[h]=s}getParentIndex(t){return t-1>>>1}getLeftIndex(t){return t*2+1}getRightIndex(t){return t*2+2}shiftUp(t){if(t===0)return;const h=this.getParentIndex(t);this.heap[h]>this.heap[t]&&(this.swap(h,t),this.shiftUp(h))}shiftDown(t){const h=this.getLeftIndex(t),s=this.getRightIndex(t);this.heap[h]<this.heap[t]&&(this.swap(h,t),this.shiftDown(h)),this.heap[s]<this.heap[t]&&(this.swap(s,t),this.shiftDown(s))}insert(t){this.heap.push(t),this.shiftUp(this.heap.length-1)}pop(){this.heap[0]=this.heap.pop(),this.shiftDown(0)}peek(){return this.heap[0]}size(){return this.heap.length}}export{i as default};
